import React, { useMemo, useEffect, useState, useRef } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import { incrementByAmount } from './counter';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const [isChecked, setIsChecked] = useState(false);
  const [selection, setSelection] = useState();
  let isStuff = '';
  let isPrice = 0;
  const counter = useSelector((state) => state.counter.value);
  const handleStuff = (e) => {
    isStuff = '';
    isPrice = 0;
    console.log(isPrice);
  };

  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();

    console.log(counter);
  };

  return (
    <>
      <form>
        <div className="content">
          {monthlyPlans.map((monthlyPlan) => {
            if (!isChecked) {
              return (
                <Yearlyslider
                  key={monthlyPlan.id}
                  {...monthlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              return <Yearlyamount key={yearlyPlan.id} {...yearlyPlan} />;
            }
          })}
          <div className="slider">
            <span>{counter}</span>
            <label className="switch">
              <input
                type="checkbox"
                onChange={() => setIsChecked((isChecked) => !isChecked)}
                value={isChecked}
              ></input>

              <span className="slide"></span>
            </label>
            <span>{selection}</span>
          </div>
          <div className="buttons">
            <button>Go Back</button>

            <button onClick={() => dispatch(incrementByAmount(isPrice))}>
              Next Step
            </button>
          </div>
        </div>
      </form>
    </>
  );
}
