import React from 'react';
import validateInfo from './Formvalidate';
import useForm from './useForm';

export default function () {
  const { handelChange, values, handelSubmit, errors } = useForm(validateInfo);
  return (
    <div>
      <form onSubmit={handelSubmit}>
        <p>Name</p> {errors.fullname && <p>{errors.fullname}</p>}
        <input
          type="text"
          name="fullname"
          placeholder="Full Name"
          value={values.fullname}
          onChange={handelChange}
        ></input>
        <p>Email Address</p>{' '}
        {errors.emailAddress && <p>{errors.emailAddress}</p>}
        <input
          type="text"
          name="emailAddress"
          placeholder="Valid Email Address"
          value={values.emailAddress}
          onChange={handelChange}
        ></input>
        <p>Phone Number</p> {errors.phoneNumber && <p>{errors.phoneNumber}</p>}
        <input
          type="number"
          name="phoneNumber"
          placeholder="Valid Phone Number"
          value={values.phoneNumber}
          onChange={handelChange}
        ></input>
        <input type="submit">Next Step</input>
      </form>
    </div>
  );
}
