import React, { useMemo, useEffect, useState, useRef } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const [isChecked, setIsChecked] = useState(false);
  const [selection, setSelection] = useState();
  const selectPlan = useRef({});
  console.log(selectPlan);
  const handleSelect = (price) => {
    selectPlan = price;
  };

  console.log(selectPlan);
  useEffect(() => {
    console.log('yo');
  });

  return (
    <div className="content">
      {monthlyPlans.map((monthlyPlan) => {
        if (!isChecked) {
          return (
            <Yearlyslider
              key={monthlyPlan.id}
              {...monthlyPlan}
              handleSelect={handleSelect}
            />
          );
        }
      })}
      {yearlyPlans.map((yearlyPlan) => {
        if (isChecked) {
          return <Yearlyamount key={yearlyPlan.id} {...yearlyPlan} />;
        }
      })}
      <div className="slider">
        <span>Monthly</span>
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
        <span>{selection}</span>
      </div>
      <div className="buttons">
        <button>Go Back</button>

        <button>Next Step</button>
      </div>
    </div>
  );
}
