import { createSlice } from '@reduxjs/toolkit';
const initialState = {
  value: 0,
};
export const summarySlice = createSlice({
  name: 'summary',
  initialState,
  reducers: {
    increment: (state) => {
      state.value += 1;
    },
  },
});
export const { increment } = summarySlice.action;
export default summarySlice.reducer;
