import React from 'react';
import { useState } from 'react';

export default function Yearlyamount(props) {
  const { name, price } = props;

  return (
    <div className="options">
      <div className="option">
        <input
          type="radio"
          className="arcade"
          name="arcade"
          id="arcade1"
          onClick={props.makeSelection('yo')}
          checked
        ></input>
        <label for="arcade1">
          <h2>{name}</h2>
          <p>{price}</p>
          <h3>2 months free</h3>
        </label>
      </div>
    </div>
  );
}
