import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { decrement, incrementByAmount } from './counter';
import Yearlyamount from './Yearlyamount';

export default function MonthlyPrices(props) {
  const { name, price, planType, id, checked, image, handleStuff } = props;
  const [isChecked, setIsChecked] = useState(true);
  const handleCheck = () => {
    if (checked) {
      return false;
    } else {
      return true;
    }
  };
  return (
    <div className="options">
      <div className="option">
        <label for={planType}>
          <input
            checked={checked}
            className="planInputs"
            type="radio"
            name="yo"
            value={price}
            id={planType}
            text-attribute={name}
            onClick={(e) => handleStuff(e)}
            onChange={handleCheck()}
          ></input>
        </label>

        <div className="planImg">
          <img src={image}></img>
        </div>
        <div className="planText">
          <h2>{name}</h2>
          <p>${price}/mo</p>
        </div>
      </div>
    </div>
  );
}
