import { createSlice } from '@reduxjs/toolkit';
const initialState = {
  value: 0,
};

export const summarySlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    increment: (state, action) => {
      state.value += 1;
    },
  },
});
export const { increment } = summarySlice.actions;
export default summarySlice.reducer;
