import React, { useState } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import {
  incrementByAmount,
  decrement,
  increment,
  option,
  removeOption,
} from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  console.log(monthlyPlans);
  const navigate = useNavigate();

  console.log(monthlyPlans);
  let isStuff = 'Arcade';
  let isPrice = 9;
  let isPlan = 'monthly';

  const handleStuff = (e) => {
    isStuff = e.target.getAttribute('text-attribute');
    isPrice = e.target.value;
    isPlan = e.target.id;
  };
  const routeChange = (path) => {
    navigate(path);
  };
  const handleMeme = () => {
    if (isChecked) {
      routeChange('/addonsyearly');
    } else {
      routeChange('/addons');
    }
  };
  {
  }
  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <form
        id="planSubmit"
        onSubmit={() =>
          dispatch(incrementByAmount(isPrice)) &&
          dispatch(decrement(isStuff)) &&
          dispatch(increment(isPlan)) &&
          handleMeme()
        }
      >
        <div className="options-container">
          <Yearlyslider
            monthlyPlans={monthlyPlans.name}
            handleStuff={handleStuff}
          />

          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              dispatch(option());
              return (
                <Yearlyamount
                  key={yearlyPlan.id}
                  {...yearlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
        </div>
      </form>
      <div className="slider">
        <h4>Monthly</h4>
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
        <h4>Yearly</h4>
      </div>
      <div className="bottom-buttons">
        <button type="button">Go Back</button>

        <button type="submit" form="planSubmit">
          Next Step
        </button>
      </div>
    </>
  );
}
