import React from 'react';
import { useState } from 'react';

export default function Yearlyamount(props) {
  const { name, price } = props;
  const handleStuff = (e) => {
    let isStuff = e.target.name;
    let isPrice = e.target.price;

    console.log(isStuff, isPrice);
  };
  return (
    <>
      <div className="options">
        <div className="option">
          <input
            type="radio"
            className="arcade"
            id="arcade1"
            name={name}
            value={price}
            checked
          ></input>
          <label for="arcade1">
            <h2>{name}</h2>
            <p>{price}</p>
            <h3>2 months free</h3>
          </label>
        </div>
      </div>
    </>
  );
}
