import React from 'react';
import validateInfo from './Formvalidate';
import useForm from './useForm';

const Form = ({ submitForm }) => {
  const { handleChange, handleSubmit, values, errors } = useForm(
    submitForm,
    validate
  );
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <p>Name</p> {errors.fullname && <p>{errors.fullname}</p>}
        <input
          type="text"
          name="fullname"
          placeholder="Full Name"
          value={values.fullname}
          onChange={handleChange}
        ></input>
        <p>Email Address</p>{' '}
        {errors.emailAddress && <p>{errors.emailAddress}</p>}
        <input
          type="text"
          name="emailAddress"
          placeholder="Valid Email Address"
          value={values.emailAddress}
          onChange={handleChange}
        ></input>
        <p>Phone Number</p> {errors.phoneNumber && <p>{errors.phoneNumber}</p>}
        <input
          type="number"
          name="phoneNumber"
          placeholder="Valid Phone Number"
          value={values.phoneNumber}
          onChange={handleChange}
        ></input>
        <input type="submit" value="Next Step"></input>
      </form>
    </div>
  );
};
export default Form;
