import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { decrement, incrementByAmount } from './counter';
import Yearlyamount from './Yearlyamount';
import Monthlyaddons from './Monthlyaddons';
export default function Addons({ monthlyAdd }) {
  let isName = '';
  let isPrice = 0;
  let isPlan = '';

  const handleStuff = (e) => {
    isStuff = e.target.name;
    isPrice = e.target.value;
    isPlan = e.target.id;
    console.log(isPrice);
  };
  return (
    <>
      <div className="content">
        <div className="content-text">
          <h1>Pick add-ons</h1>
          <p>Add-ons help enchance your gaming experience.</p>
        </div>
        {monthlyAdd.map((addOns) => {
          return <Monthlyaddons key={addOns.id} {...addOns}></Monthlyaddons>;
        })}
        <div className="buttons">
          <button>Go Back</button>
          <button>Next Step</button>
        </div>
      </div>
    </>
  );
}
