  const [isChecked, setIsChecked] = useState(false);
  console.log(selection);
  return (
    <div className="options">
      <div className="option">
        <label for="arcade1">
          <input
            type="radio"
            className="arcade"
            name="arcade"
            id="arcade1"
            onChange={setSelection({ name, price })}
            checked
          ></input>
          <h2>{name}</h2>
          <p>{price}</p>
        </label>
      </div>
    </div>
  );
}
