    }
  });
  return (
    <div className="options">
      <div className="option">
        <label for={planType}>
          <input
            type="radio"
            name="yo"
            value={price}
            id={planType}
            text-attribute={name}
            onChange={(e) => handleStuff(e)}
          ></input>
        </label>

        <div className="planImg">
          <img src={image}></img>
        </div>
        <div className="planText">
          <h2>{name}</h2>
          <p>${price}/mo</p>
        </div>
      </div>
    </div>
  );
}
