import React, { useState } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import arcadeImage from '../../src/images/icon-arcade.svg';
import advancedImage from '../../src/images/icon-advanced.svg';
import proImage from '../../src/images/icon-pro.svg';

import {
  incrementByAmount,
  decrement,
  increment,
  option,
  removeOption,
} from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({}) {
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(false);
  const image1 = arcadeImage;
  const image2 = advancedImage;
  const image3 = proImage;
  let isStuff = 'Arcade';
  let isPrice = 9;
  let isPlan = 'monthly';

  const handleStuff = (e) => {
    isStuff = e.target.getAttribute('text-attribute');
    isPrice = e.target.value;
    isPlan = e.target.id;
  };
  const routeChange = (path) => {
    navigate(path);
  };
  const handleMeme = () => {
    if (isChecked) {
      routeChange('/addonsyearly');
    } else {
      routeChange('/addons');
    }
  };
  {
  }
  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();
  };

  const monthlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 9,
      planType: 'monthly',
      image: image1,
      checked: true,
    },
    {
      id: 2,
      name: 'Advanced',
      price: 12,
      planType: 'monthly',
      image: image2,
      checked: false,
    },
    {
      id: 3,
      name: 'Pro',
      price: 15,
      planType: 'monthly',
      image: image3,
      checked: false,
    },
  ];
  const yearlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 90,
      text: '2 months free',
      planType: 'yearly',
      image: image1,
    },
    {
      id: 2,
      name: 'Advanced',
      price: 120,
      planType: 'yearly',
      image: image2,
    },
    {
      id: 3,
      name: 'Pro',
      price: 150,
      planType: 'yearly',
      image: image3,
    },
  ];

  return (
    <>
      <form
        id="planSubmit"
        onSubmit={() =>
          dispatch(incrementByAmount(isPrice)) &&
          dispatch(decrement(isStuff)) &&
          dispatch(increment(isPlan)) &&
          handleMeme()
        }
      >
        <div className="options-container">
          <Yearlyslider
            monthlyOptions={monthlyOptions}
            handleStuff={handleStuff}
          />
          / /
          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              dispatch(option());
              return (
                <Yearlyamount
                  key={yearlyPlan.id}
                  {...yearlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
          ---
        </div>
      </form>
      <div className="slider">
        <h4>Monthly</h4>
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
        <h4>Yearly</h4>
      </div>
      <div className="bottom-buttons">
        <button type="button">Go Back</button>

        <button type="submit" form="planSubmit">
          Next Step
        </button>
      </div>
    </>
  );
}
