import React, { useMemo, useEffect, useState, useRef } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import { incrementByAmount, decrement, increment } from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(false);

  let isStuff = '';
  let isPrice = 0;
  let isPlan = '';

  const handleStuff = (e) => {
    isStuff = e.target.name;
    isPrice = e.target.value;
    isPlan = e.target.id;
    console.log(isPrice);
  };
  const routeChange = (path) => {
    navigate(path);
  };
  const handleMeme = () => {
    if (isChecked) {
      routeChange('/addonsyearly');
    } else {
      routeChange('/addons');
    }
  };
  {
  }
  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();

    console.log('counter');
  };

  return (
    <>
      <form
        onSubmit={() =>
          dispatch(incrementByAmount(isPrice)) &&
          dispatch(decrement(isStuff)) &&
          dispatch(increment(isPlan)) &&
          handleMeme()
        }
      >
        <div className="content">
          {monthlyPlans.map((monthlyPlan) => {
            if (!isChecked) {
              return (
                <Yearlyslider
                  key={monthlyPlan.id}
                  {...monthlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              return <Yearlyamount key={yearlyPlan.id} {...yearlyPlan} />;
            }
          })}
          <div className="slider">
            <span></span>
            <label className="switch">
              <input
                type="checkbox"
                onChange={() => setIsChecked((isChecked) => !isChecked)}
                value={isChecked}
              ></input>

              <span className="slide"></span>
            </label>
          </div>
          <div className="buttons">
            <button type="button">Go Back</button>

            <button type="submit">Next Step</button>
          </div>
        </div>
      </form>
    </>
  );
}
