import React, { useState } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';

import {
  incrementByAmount,
  decrement,
  increment,
  option,
  removeOption,
} from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({}) {
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(false);

  let isStuff = 'Arcade';
  let isPrice = 9;
  let isPlan = 'monthly';

  const handleStuff = (e) => {
    isStuff = e.target.getAttribute('name-attribute');

    isPrice = e.target.value;
    isPlan = e.target.getAttribute('text-attribute');
  };
  const routeChange = (path) => {
    navigate(path);
  };
  const handleMeme = () => {
    if (isChecked) {
      routeChange('/addonsyearly');
    } else {
      routeChange('/addons');
    }
  };
  {
  }
  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();
  };

  const monthlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 9,
      planType: 'monthly',
    },
    {
      id: 2,
      name: 'Advanced',
      price: 12,
      planType: 'monthly',
    },
    {
      id: 3,
      name: 'Pro',
      price: 15,
      planType: 'monthly',
    },
  ];
  const yearlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 90,
      text: '2 months free',
      planType: 'yearly',
    },
    {
      id: 2,
      name: 'Advanced',
      price: 120,
      planType: 'yearly',
    },
    {
      id: 3,
      name: 'Pro',
      price: 150,
      planType: 'yearly',
    },
  ];

  return (
    <>
      <form
        id="planSubmit"
        onSubmit={() =>
          dispatch(incrementByAmount(isPrice)) &&
          dispatch(decrement(isStuff)) &&
          dispatch(increment(isPlan)) &&
          handleMeme()
        }
      >
        <div className="options-container">
          {isChecked ? (
            <Yearlyslider
              monthlyOptions={monthlyOptions}
              handleStuff={handleStuff}
            />
          ) : (
            <Yearlyamount
              yearlyyOptions={yearlyOptions}
              handleStuff={handleStuff}
            />
          )}
        </div>
      </form>
      <div className="slider">
        <h4>Monthly</h4>
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
        <h4>Yearly</h4>
      </div>
      <div className="bottom-buttons">
        <button type="button">Go Back</button>

        <button type="submit" form="planSubmit">
          Next Step
        </button>
      </div>
    </>
  );
}
