import React, { useMemo, useEffect, useState } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const [isChecked, setIsChecked] = useState(false);
  const [selection, setSelection] = useState();
  console.log(selection);

  return (
    <div className="content">
      {monthlyPlans.map((monthlyPlan) => {
        if (!isChecked) {
          return (
            <Yearlyslider
              makeSelection={(selection) => setSelection(selection)}
              key={monthlyPlan.id}
              {...monthlyPlan}
            />
          );
        }
      })}
      {yearlyPlans.map((yearlyPlan) => {
        if (isChecked) {
          return <Yearlyamount key={yearlyPlan.id} {...yearlyPlan} />;
        }
      })}
      <div className="slider">
        <span>Monthly</span>
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
        <span>{selection}</span>
      </div>
      <div className="buttons">
        <button>Go Back</button>

        <button>Next Step</button>
      </div>
    </div>
  );
}
