import React, { useMemo, useEffect, useState, useRef } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import { incrementByAmount } from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const [isChecked, setIsChecked] = useState(false);

  let isStuff = '';
  let isPrice = 0;
  const navigate = useNavigate();
  const counter = useSelector((state) => state.counter.value);
  const handleStuff = (e) => {
    isStuff = e.target.name;
    isPrice = e.target.value;
    console.log(isPrice);
  };

  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();

    console.log('counter');
  };

  return (
    <>
      <form
        onSubmit={
          (() => dispatch(incrementByAmount(isStuff, isPrice)),
          navigate('/yourinfo'))
        }
      >
        <div className="content">
          {monthlyPlans.map((monthlyPlan) => {
            if (!isChecked) {
              return (
                <Yearlyslider
                  key={monthlyPlan.id}
                  {...monthlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              return <Yearlyamount key={yearlyPlan.id} {...yearlyPlan} />;
            }
          })}
          <div className="slider">
            <span></span>
            <label className="switch">
              <input
                type="checkbox"
                onChange={() => setIsChecked((isChecked) => !isChecked)}
                value={isChecked}
              ></input>

              <span className="slide"></span>
            </label>
          </div>
          <div className="buttons">
            <button>Go Back</button>

            <button type="submit">Next Step</button>
          </div>
          <h1>{counter}</h1>
        </div>
      </form>
    </>
  );
}
