import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import addOnName from './counter';
import Yearlyamount from './Yearlyamount';
import Monthlyaddons from './Monthlyaddons';
export default function Addons({ monthlyAdd }) {
  const planName = useSelector((state) => state.counter.addOns.name);
  const handleTest = () => {
    console.log(planName);
  };
  let isName = '';
  let isCost = 0;
  let isText = '';
  const dispatch = useDispatch();
  const handleStuff = (e) => {
    isName = e.target.name;
    isCost = e.target.value;
    isText = e.target.id;
    console.log(planName);
  };

  return (
    <>
      <form>
        <div className="content">
          <div className="content-text">
            <h1>Pick add-ons</h1>
            <p>Add-ons help enchance your gaming experience.</p>
          </div>
          {monthlyAdd.map((addOns) => {
            return (
              <Monthlyaddons
                key={addOns.id}
                {...addOns}
                handleStuff={handleStuff}
              ></Monthlyaddons>
            );
          })}
          <div className="buttons">
            <button onClick={dispatch(addOnName(isName))}>Go Back</button>
            <button>Next Step</button>
          </div>
        </div>
      </form>
    </>
  );
}
