import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { increment, addOnName, addOnCost, addOnText } from './counter';
import Yearlyamount from './Yearlyamount';
import Monthlyaddons from './Monthlyaddons';
export default function Addons({ monthlyAdd }) {
  const planName = useSelector((state) => state.counter.addOns.name);
  const navigate = useNavigate();
  let isNameArr = [];
  let isCost = 0;
  let isText = '';
  const dispatch = useDispatch();
  const handleStuff = (e) => {
    let isName = e.target.name;
    isName.push(isNameArr);
    isCost = e.target.value;
    isText = e.target.id;
    console.log(isName);
  };

  const routeChange = (path) => {
    navigate(path);
  };
  const changeRoute = () => {
    routeChange('/summary');
  };

  return (
    <>
      <form
        onSubmit={() => {
          dispatch(addOnName(isNameArr)) &&
            dispatch(addOnCost(isCost)) &&
            dispatch(addOnText(isText)) &&
            changeRoute();
        }}
      >
        <div className="content">
          <div className="content-text">
            <h1>Pick add-ons</h1>
            <p>Add-ons help enchance your gaming experience.</p>
          </div>
          {monthlyAdd.map((addOns) => {
            return (
              <Monthlyaddons
                key={addOns.id}
                {...addOns}
                handleStuff={handleStuff}
              ></Monthlyaddons>
            );
          })}
          <div className="buttons">
            <button type="submit">Go Back</button>
            <button>Next Step</button>
          </div>
        </div>
      </form>
    </>
  );
}
