import React from 'react';
import arcadeImage from '../../src/images/icon-arcade.svg';
import advancedImage from '../../src/images/icon-advanced.svg';
import proImage from '../../src/images/icon-pro.svg';

import Slider from './Slider';

export default function Selectplan({}) {
  const image1 = arcadeImage;
  const image2 = advancedImage;
  const image3 = proImage;

  const monthlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 9,
      planType: 'monthly',
      image: image1,
    },
    {
      id: 2,
      name: 'Advanced',
      price: 12,
      planType: 'monthly',
      image: image2,
    },
    {
      id: 3,
      name: 'Pro',
      price: 15,
      planType: 'monthly',
      image: image3,
    },
  ];
  const yearlyOptions = [
    {
      id: 1,
      name: 'Arcade',
      price: 90,
      text: '2 months free',
      planType: 'yearly',
      image: image1,
    },
    {
      id: 2,
      name: 'Advanced',
      price: 120,
      planType: 'yearly',
      image: image2,
    },
    {
      id: 3,
      name: 'Pro',
      price: 150,
      planType: 'yearly',
      image: image3,
    },
  ];

  return (
    <div className="content">
      <div className="content-text">
        <h1>Select your plan</h1>
        <p>You have the option of monthly or yearly billing</p>
      </div>

      <Slider
        monthlyPlans={monthlyOptions}
        yearlyPlans={yearlyOptions}
      ></Slider>
    </div>
  );
}
