import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { decrement, incrementByAmount } from './counter';
import Yearlyamount from './Yearlyamount';

export default function MonthlyPrices(props) {
  const { name, price, planType, id, image, handleStuff } = props;
  const [isChecked, setIsChecked] = useState(0);
  const arcade = document.querySelectorAll('input[type=radio]');
  console.log(arcade);
  return (
    <div className="options">
      <div className="option">
        <label for={planType}>
          <input
            checked="Arcade"
            type="radio"
            name="yo"
            value={price}
            id={planType}
            text-attribute={name}
            onClick={(e) => handleStuff(e)}
          ></input>
        </label>

        <div className="planImg">
          <img src={image}></img>
        </div>
        <div className="planText">
          <h2>{name}</h2>
          <p>${price}/mo</p>
        </div>
      </div>
    </div>
  );
}
