import { useState, useEffect } from 'react';
const useForm = () => {
  const [values, setValues] = useState({
    fullName: '',
    emailAddress: '',
    phoneNumber: '',
  });
  const [errors, setErrors] = useState({});

  const handelChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
    console.log({ name });
  };

  return { handelChange, values };
};
export default useForm;
