import React, { useState } from 'react';
import Yearlyslider from './Yearlyslider';
import Yearlyamount from './Yearlyamount';
import { useDispatch, useSelector } from 'react-redux';
import {
  incrementByAmount,
  decrement,
  increment,
  option,
  removeOption,
} from './counter';
import { useNavigate } from 'react-router-dom';
export default function Slider({ monthlyPlans, yearlyPlans }) {
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(false);
  console.log(monthlyPlans);
  let isStuff = '';
  let isPrice = 0;
  let isPlan = '';

  const handleStuff = (e) => {
    isStuff = e.target.name;
    isPrice = e.target.value;
    isPlan = e.target.id;
  };
  const routeChange = (path) => {
    navigate(path);
  };
  const handleMeme = () => {
    if (isChecked) {
      routeChange('/addonsyearly');
    } else {
      routeChange('/addons');
    }
  };
  {
  }
  const dispatch = useDispatch();
  const handleSelection = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <form
        onSubmit={() =>
          dispatch(incrementByAmount(isPrice)) &&
          dispatch(decrement(isStuff)) &&
          dispatch(increment(isPlan)) &&
          handleMeme()
        }
      >
        <div className="options-container">
          {monthlyPlans.map((monthlyPlan) => {
            if (!isChecked) {
              dispatch(removeOption());
              return (
                <Yearlyslider
                  key={monthlyPlan.id}
                  {...monthlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
          {yearlyPlans.map((yearlyPlan) => {
            if (isChecked) {
              dispatch(option());
              return (
                <Yearlyamount
                  key={yearlyPlan.id}
                  {...yearlyPlan}
                  handleStuff={handleStuff}
                />
              );
            }
          })}
        </div>
      </form>
      <div className="slider">
        <label className="switch">
          <input
            type="checkbox"
            onChange={() => setIsChecked((isChecked) => !isChecked)}
            value={isChecked}
          ></input>

          <span className="slide"></span>
        </label>
      </div>
      <div className="bottom-button">
        <button type="submit">Next Step</button>
      </div>
    </>
  );
}
